clear;clc oA1=[' E:\Dropbox\Datasets\Patents\codeReplication2\Figs\Y_A1.csv' ];oA2=[' E:\Dropbox\Datasets\Patents\codeReplication2\Figs\Y_A2.csv' ];oA3=[' E:\Dropbox\Datasets\Patents\codeReplication2\Figs\X_A1.csv' ];oA4=[' E:\Dropbox\Datasets\Patents\codeReplication2\Figs\X_A2.csv' ]; t1c=strrep(csvimport(oA1),'"','');t2c=strrep(csvimport(oA2),'"','');t3c=strrep(csvimport(oA3),'"','');t4c=strrep(csvimport(oA4),'"','');   SR=3;SC=2;ER=4;EC=6;for i=1:ER-SR+1    for j=1:EC-SC+1                t1(i,j)=str2num(cell2mat(t1c(SR-1+i,SC-1+j)));        t2(i,j)=str2num(cell2mat(t2c(SR-1+i,SC-1+j)));        t3(i,j)=str2num(cell2mat(t3c(SR-1+i,SC-1+j)));        t4(i,j)=str2num(cell2mat(t4c(SR-1+i,SC-1+j)));                     endendtmp = cell(0);tmp{1} = 'xticklabel style={/pgf/number format/fixed}';  % this is important to prevent scientific formattmp{2} = 'yticklabel style={/pgf/number format/fixed}';  % for numbers along the tick markstmp{3} = 'scale only axis,';tmp{4} = 'axis lines=left';t1=[zeros(2,1) t1];t2=[zeros(2,1) t2];t3=[zeros(2,1) t3];t4=[zeros(2,1) t4];figure(1);plot([0:5],  [t1(1,1:6)],'k','Linewidth',1.5);hold on; legend('hide')jbfill([0:5], [t1(1,1:6)+1.67*t1(2,1:6)],[t1(1,1:6)-1.67*t1(2,1:6)],[0.7 0.7 0.7],[0 0 0], 0,0.5);hold off; matlab2tikz('E:\Dropbox\Datasets\Patents\codeReplication2\Figs\Y_A1.tikz','height', '\fheight', ...    'width', '\fwidth','showInfo', false,...    'extraAxisOptions',tmp,'parseStrings',false);figure(2);plot([0:5],  [t2(1,1:6)],'k','Linewidth',1.5); hold on; legend('hide')jbfill([0:5], [t2(1,1:6)+1.67*t2(2,1:6)],[t2(1,1:6)-1.67*t2(2,1:6)],[0.7 0.7 0.7],[0 0 0], 0,0.5);hold off; matlab2tikz('E:\Dropbox\Datasets\Patents\codeReplication2\Figs.tikz','height', '\fheight', ...    'width', '\fwidth','showInfo', false,...    'extraAxisOptions',tmp,'parseStrings',false); figure(3);plot([0:5],  [t3(1,1:6)],'k','Linewidth',1.5); hold on; legend('hide')jbfill([0:5], [t3(1,1:6)+1.67*t3(2,1:6)],[t3(1,1:6)-1.67*t3(2,1:6)],[0.7 0.7 0.7],[0 0 0], 0,0.5);hold off; matlab2tikz('E:\Dropbox\Datasets\Patents\codeReplication2\Figs\X_A1.tikz','height', '\fheight', ...    'width', '\fwidth','showInfo', false,...    'extraAxisOptions',tmp,'parseStrings',false); figure(4);plot([0:5],  [t4(1,1:6)],'k','Linewidth',1.5); hold on; legend('hide')jbfill([0:5], [t4(1,1:6)+1.67*t4(2,1:6)],[t4(1,1:6)-1.67*t4(2,1:6)],[0.7 0.7 0.7],[0 0 0], 0,0.5);hold off; matlab2tikz('E:\Dropbox\Datasets\Patents\codeReplication2\Figs\X_A2.tikz','height', '\fheight', ...    'width', '\fwidth','showInfo', false,...    'extraAxisOptions',tmp,'parseStrings',false); 